import React, {Fragment, useState} from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Search from './components/users/Search';
import Users from './components/users/Users';
import User from './components/users/User';
import Alert from './components/layout/Alert';
import About from './components/pages/About';
import GithubState from './context/github/GithubState';
import './App.css';

const App = () => {
const [users, setUsers] = useState([]);
const [repos, setRepos] = useState([]);
const [user, setUser] = useState({});
const [loading, setLoading] = useState(false);
const [alert, setAlert] = useState(null);

//Get single Github User
const getUser = (username) => {
  setLoading(true);

  fetch(`https://api.github.com/users/${username}?client_id=
  ${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  .then((res) => {
    return res.json();
  })
  .then((data) => {
    setUser(data);
    setLoading(false);
  });
}

//Get Users Repos
const getUserRepos = (username) => {
  setLoading(true);

  fetch(`https://api.github.com/users/${username}/repos?per_page=5&sort=created:asc$client_id=
  ${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  .then((res) => {
    return res.json();
  })
  .then((data) => {
    setRepos(data);
    setLoading(false);
  });
}

//Clear users from state
const clearUsers = () => {
  setUsers([]);
  setLoading(false);
}

//Set Alert
const showAlert = (msg, type) => {
  setAlert({msg: msg, type: type});

  setTimeout(() => setAlert(null), 5000);
}
    return (
      <GithubState>
      <Router>
      <div className='App'>
        <Navbar/>
        <div className="container">
        <Alert alert={alert}/>
        <Switch>
          <Route exact path='/' render={props => (
            <Fragment>
              <Search clearUsers={clearUsers} 
              showClear={ users.length > 0 ? true: false } setAlert={showAlert}/>
              <Users/>
            </Fragment>
          )}/>

          <Route exact path='/about' component={About}/>
          <Route exact path='/user/:login' render={props => (
          <User {...props} getUser={getUser} getUserRepos={getUserRepos} 
          repos={repos} user={user} loading={loading}/>
          )}/>
        </Switch>
        </div>
      </div>
      </Router>
      </GithubState>
       );
}

export default App;
