import React, {Component, Fragment} from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Search from './components/users/Search';
import Users from './components/users/Users';
import User from './components/users/User';
import Alert from './components/layout/Alert';
import About from './components/pages/About';
import './App.css';

class App extends Component {
  state = {
    users: [],
    user: {},
    loading: false,
    alert: null,
  }

//Search Github Users
searchUsers = (text) =>{
  this.setState({loading: true});

  fetch(`https://api.github.com/search/users?q=${text}&client_id=
  ${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  .then((res) => {
    return res.json();
  })
  .then((data) => {
    this.setState({ users: data.items, loading: false});
  });
}

//Get single Github User
getUser = (username) => {
  this.setState({loading: true});

  fetch(`https://api.github.com/users/${username}?client_id=
  ${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  .then((res) => {
    return res.json();
  })
  .then((data) => {
    this.setState({ user: data.data, loading: false });
  });
}

//Clear users from state
clearUsers = () => this.setState({ users:[], loading: false });

//Set Alert
setAlert = (msg, type) => {
  this.setState({ alert: {msg: msg, type: type} });

  setTimeout(() => this.setState({ alert: null}), 5000);
}

  render(){
    const { users, user, loading} = this.state;

    return (
      <Router>
      <div className='App'>
        <Navbar/>
        <div className="container">
        <Alert alert={this.state.alert}/>
        <Switch>
          <Route exact path='/' render={props => (
            <Fragment>
              <Search searchUsers={this.searchUsers} clearUsers={this.clearUsers} 
              showClear={ users.length > 0 ? true: false } setAlert={this.setAlert}/>
              <Users loading={loading} users={users} />
            </Fragment>
          )}/>

          <Route exact path='/about' component={About}/>
          <Route exact path='/user/:login' render={props => (
          <User {...props} getUser={this.getUser} user={user} loading={loading}/>
          )}/>
        </Switch>
        </div>
      </div>
      </Router>
       );
  }
}

export default App;
